commits.content:
.app-sidebar {
  width: 244px;
  background-color: #2C3A47;
  display: flex;
  flex-direction: column;
  padding: 20px 0;
  box-sizing: border-box;
  flex-shrink: 0;
  border-right: 1px solid #DDC9D4;
  position: fixed;
  top: 0;
  left: max(0px, calc(50% - 720px));
  height: 100vh;
  overflow-y: auto;
  z-index: 101;
}

.app-sidebar-logo {
  font-family: 'Praise', cursive;
  font-size: 32px;
  font-weight: 400;
  color: #fff;
  padding: 0 24px;
  margin-bottom: 30px;
}

.app-sidebar-nav {
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 0 12px;
}

.app-sidebar-nav-item {
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 12px;
  text-decoration: none;
  color: #fff;
  font-size: 16px;
  font-family: 'Inter', sans-serif;
  border-radius: 8px;
  transition: all 0.2s;
}

.app-sidebar-nav-item:hover {
  background-color: rgba(0, 169, 190, 0.1);
  color: #00A9BE;
}

.app-sidebar-nav-item.active {
  background-color: rgba(0, 169, 190, 0.15);
  color: #00A9BE;
  font-weight: 600;
}

.app-sidebar-nav-icon {
  width: 24px;
  height: 24px;
  color: inherit;
  flex-shrink: 0;
  transition: color 0.2s;
}

.app-sidebar-current-user {
  margin-top: 24px;
  padding: 10px 16px;
  display: flex;
  align-items: center;
  gap: 10px;
  text-decoration: none;
  color: #ffffff;
  font-family: 'Inter', sans-serif;
}

.app-sidebar-current-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  overflow: hidden;
  background-color: #DDC9D4;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

.app-sidebar-current-avatar img,
.app-sidebar-current-avatar video {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
}

.app-sidebar-current-initial {
  font-size: 16px;
  font-weight: 600;
  color: #2C3A47;
}

.app-sidebar-current-info {
  display: flex;
  flex-direction: column;
}

.app-sidebar-current-name {
  font-size: 14px;
  font-weight: 600;
}

.app-sidebar-current-username {
  font-size: 12px;
  color: #B8B0C1;
}

.app-sidebar-bubbles {
  position: relative;
  flex: 1;
  width: 100%;
  overflow: hidden;
  margin-top: auto;
}

.app-sidebar-bubble {
  position: absolute;
  border-radius: 50%;
  background: radial-gradient(
    circle at 30% 30%,
    rgba(0, 169, 190, 0.4) 0%,
    rgba(221, 201, 212, 0.3) 30%,
    rgba(184, 176, 193, 0.25) 60%,
    rgba(247, 232, 228, 0.2) 100%
  );
  border: 1px solid rgba(221, 201, 212, 0.3);
  backdrop-filter: blur(2px);
  animation: appSidebarBubbleFloat 15s infinite ease-in-out;
  opacity: 0.7;
  box-shadow:
    inset 0 0 20px rgba(0, 169, 190, 0.2),
    0 0 10px rgba(221, 201, 212, 0.15);
}

.app-sidebar-bubble-1 { width: 30px; height: 30px; left: 20%; bottom: 10%; animation-delay: 0s; animation-duration: 18s; }
.app-sidebar-bubble-2 { width: 45px; height: 45px; left: 60%; bottom: 15%; animation-delay: 2s; animation-duration: 20s; }
.app-sidebar-bubble-3 { width: 25px; height: 25px; left: 40%; bottom: 5%; animation-delay: 4s; animation-duration: 16s; }
.app-sidebar-bubble-4 { width: 35px; height: 35px; left: 75%; bottom: 8%; animation-delay: 6s; animation-duration: 22s; }
.app-sidebar-bubble-5 { width: 40px; height: 40px; left: 10%; bottom: 12%; animation-delay: 8s; animation-duration: 19s; }
.app-sidebar-bubble-6 { width: 28px; height: 28px; left: 50%; bottom: 3%; animation-delay: 10s; animation-duration: 17s; }

@keyframes appSidebarBubbleFloat {
  0% {
    transform: translateY(0) translateX(0) scale(1);
    opacity: 0.6;
  }
  25% {
    transform: translateY(-25vh) translateX(10px) scale(1.1);
    opacity: 0.8;
  }
  50% {
    transform: translateY(-50vh) translateX(-5px) scale(0.9);
    opacity: 0.7;
  }
  75% {
    transform: translateY(-75vh) translateX(8px) scale(1.05);
    opacity: 0.5;
  }
  100% {
    transform: translateY(-100vh) translateX(0) scale(0.8);
    opacity: 0;
  }
}

.app-layout-with-sidebar {
  width: 1440px;
  max-width: 100%;
  min-height: 100vh;
  margin: 0 auto;
  display: flex;
  box-sizing: border-box;
}

.app-layout-with-sidebar .app-layout-main {
  flex: 1;
  min-width: 0;
  margin-left: 244px;
}
