.bubble-animation-container {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  pointer-events: none;
  z-index: 2;
}

.bubble {
  position: absolute;
  bottom: -100px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 2px solid rgba(255, 255, 255, 0.3);
  box-shadow: 
    0 0 20px rgba(255, 255, 255, 0.2),
    inset 0 0 20px rgba(255, 255, 255, 0.1);
  animation: floatUp linear infinite;
  display: flex;
  align-items: center;
  justify-content: center;
}

.bubble-inner {
  width: 80%;
  height: 80%;
  border-radius: 50%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.3);
}

.bubble-avatar {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
}

@keyframes floatUp {
  0% {
    transform: translateY(0) translateX(0) scale(1);
    opacity: 0.7;
  }
  10% {
    transform: translateY(-10vh) translateX(5px) scale(1.05);
    opacity: 1;
  }
  50% {
    transform: translateY(-50vh) translateX(-10px) scale(1.1);
    opacity: 0.9;
  }
  90% {
    transform: translateY(-90vh) translateX(5px) scale(1.05);
    opacity: 0.6;
  }
  100% {
    transform: translateY(-100vh) translateX(0) scale(1);
    opacity: 0;
  }
}


.bubble::before {
  content: '';
  position: absolute;
  top: 20%;
  left: 20%;
  width: 30%;
  height: 30%;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.4);
  filter: blur(5px);
}

.bubble::after {
  content: '';
  position: absolute;
  top: 10%;
  right: 15%;
  width: 20%;
  height: 20%;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  filter: blur(3px);
}
