commits.content:
.emoji-picker-wrap {
  position: relative;
  flex-shrink: 0;
}

.emoji-picker-btn {
  width: 36px;
  height: 36px;
  border: none;
  border-radius: 8px;
  background: rgba(0, 0, 0, 0.06);
  font-size: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
  transition: background 0.2s;
}

.emoji-picker-btn:hover:not(:disabled) {
  background: rgba(0, 0, 0, 0.1);
}

.emoji-picker-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.emoji-picker-panel {
  position: absolute;
  bottom: 100%;
  left: 0;
  margin-bottom: 6px;
  padding: 10px;
  box-sizing: border-box;
  width: 224px; 
  background: #fff;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
  display: grid;
  grid-template-columns: repeat(5, 36px);
  grid-auto-rows: 36px;
  gap: 6px;
  z-index: 100;
  max-height: 286px; 
  overflow-y: auto;
  overflow-x: hidden;
}

.emoji-picker-item {
  width: 36px;
  height: 36px;
  min-width: 36px;
  min-height: 36px;
  border: none;
  border-radius: 8px;
  background: transparent;
  font-size: 22px;
  line-height: 1;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
  transition: background 0.15s;
  box-sizing: border-box;
  flex-shrink: 0;
}

.emoji-picker-item:hover {
  background: rgba(0, 0, 0, 0.08);
}
