commits.content:
.global-search-overlay {
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.65);
  z-index: 998;
  animation: global-search-fadeIn 0.3s ease-out;
  cursor: pointer;
}

@keyframes global-search-fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.global-search-panel {
  position: fixed;
  left: max(244px, calc(50% - 720px + 244px));
  top: 0;
  width: min(400px, calc(100vw - max(244px, calc(50% - 720px + 244px)) - 16px));
  min-width: 280px;
  height: 100vh;
  background-color: rgba(255, 255, 255, 0.85);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border-right: 2px solid rgba(221, 201, 212, 0.5);
  border-top-right-radius: 15px;
  border-bottom-right-radius: 15px;
  display: flex;
  flex-direction: column;
  z-index: 999;
  box-shadow: 2px 0 8px rgba(0, 0, 0, 0.1), 0 0 0 1px rgba(255, 255, 255, 0.9);
  animation: global-search-slideIn 0.3s ease-out;
  overflow: hidden;
  box-sizing: border-box;
}

@keyframes global-search-slideIn {
  from { transform: translateX(-100%); opacity: 0; }
  to { transform: translateX(0); opacity: 1; }
}

.global-search-panel-header {
  padding: 20px 24px;
  border-bottom: 1px solid #DDC9D4;
  background-color: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
}

.global-search-panel-title {
  font-size: 24px;
  font-weight: 600;
  color: #2C3A47;
  font-family: 'Inter', sans-serif;
  margin: 0;
}

.global-search-input-container {
  position: relative;
  padding: 20px 24px;
  display: flex;
  align-items: center;
  background-color: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  min-width: 0;
}

.global-search-input-icon {
  position: absolute;
  left: 36px;
  width: 20px;
  height: 20px;
  color: #B8B0C1;
  pointer-events: none;
  z-index: 1;
}

.global-search-input {
  width: 100%;
  min-width: 0;
  height: 40px;
  padding: 0 40px 0 50px;
  background-color: #fafafa;
  border: 1px solid #DDC9D4;
  border-radius: 8px;
  font-size: 14px;
  font-family: 'Inter', sans-serif;
  color: #2C3A47;
  outline: none;
  box-sizing: border-box;
  transition: border-color 0.2s;
}

.global-search-input:focus {
  border-color: #00A9BE;
  background-color: #fff;
}

.global-search-input::placeholder {
  color: #B8B0C1;
}

.global-search-clear-btn {
  position: absolute;
  right: 36px;
  width: 20px;
  height: 20px;
  background: none;
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #B8B0C1;
  padding: 0;
  transition: color 0.2s;
}

.global-search-clear-btn:hover {
  color: #2C3A47;
}

.global-search-clear-btn svg {
  width: 16px;
  height: 16px;
}

.global-search-recent-section {
  flex: 1;
  overflow-y: auto;
  padding: 0 24px 20px;
  background-color: transparent;
}

.global-search-recent-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
}

.global-search-recent-title {
  font-size: 16px;
  font-weight: 600;
  color: #2C3A47;
  font-family: 'Inter', sans-serif;
  margin: 0;
}

.global-search-clear-recent-btn {
  background: none;
  border: none;
  color: #00A9BE;
  font-size: 14px;
  font-weight: 600;
  font-family: 'Inter', sans-serif;
  cursor: pointer;
  padding: 0;
  transition: color 0.2s;
}

.global-search-clear-recent-btn:hover {
  color: #2C3A47;
}

.global-search-recent-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.global-search-recent-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px;
  border-radius: 8px;
  cursor: pointer;
  transition: background-color 0.2s;
  text-decoration: none;
  color: inherit;
}

.global-search-recent-item:hover {
  background-color: #fafafa;
}

.global-search-recent-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: linear-gradient(135deg, #DDC9D4 0%, #B8B0C1 100%);
  flex-shrink: 0;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.global-search-recent-avatar-placeholder {
  color: #fff;
  font-size: 16px;
  font-weight: 600;
  font-family: 'Inter', sans-serif;
}

.global-search-recent-username {
  font-size: 14px;
  font-weight: 500;
  color: #2C3A47;
}

.global-search-results {
  flex: 1;
  padding: 20px 24px;
  overflow-y: auto;
  background-color: transparent;
}

.global-search-loading {
  text-align: center;
  padding: 40px 0;
  font-size: 14px;
  color: #B8B0C1;
  font-family: 'Inter', sans-serif;
}

.global-search-results-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.global-search-result-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px;
  border-radius: 8px;
  cursor: pointer;
  transition: background-color 0.2s;
  text-decoration: none;
  color: inherit;
}

.global-search-result-item:hover {
  background-color: #fafafa;
}

.global-search-result-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: linear-gradient(135deg, #DDC9D4 0%, #B8B0C1 100%);
  flex-shrink: 0;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
}

.global-search-result-avatar img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.global-search-result-avatar-placeholder {
  color: #fff;
  font-size: 16px;
  font-weight: 600;
  font-family: 'Inter', sans-serif;
}

.global-search-result-info {
  display: flex;
  flex-direction: column;
  gap: 4px;
  flex: 1;
  min-width: 0;
}

.global-search-result-username {
  font-size: 14px;
  font-weight: 600;
  color: #2C3A47;
}

.global-search-result-fullname {
  font-size: 13px;
  color: #B8B0C1;
  font-family: 'Inter', sans-serif;
}

.global-search-no-results {
  text-align: center;
  padding: 40px 0;
}

.global-search-no-results p {
  font-size: 14px;
  color: #B8B0C1;
  font-family: 'Inter', sans-serif;
  margin: 0;
}

/* Responsive Styles */

/* Desktop and Laptop (1024px - 1439px) */
@media screen and (max-width: 1439px) {
  .global-search-panel {
    left: max(200px, calc(50% - 600px + 200px));
    width: min(380px, calc(100vw - max(200px, calc(50% - 600px + 200px)) - 16px));
  }
}

/* Tablet Landscape (1024px) */
@media screen and (max-width: 1024px) {
  .global-search-panel {
    left: 80px;
    width: min(360px, calc(100vw - 80px - 16px));
  }
}

/* Tablet Portrait and Mobile (768px and below) */
@media screen and (max-width: 768px) {
  .global-search-overlay {
    display: none;
  }

  .global-search-panel {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    max-width: 100%;
    height: 100vh;
    border-radius: 0;
    transform: translateX(-100%);
    transition: transform 0.3s ease;
    box-shadow: 2px 0 8px rgba(0, 0, 0, 0.1);
  }

  .global-search-panel.open {
    transform: translateX(0);
  }

  .global-search-panel-header {
    padding: 16px 20px;
  }

  .global-search-panel-title {
    font-size: 20px;
  }

  .global-search-input-container {
    padding: 16px 20px;
  }

  .global-search-input {
    height: 36px;
    padding: 0 36px 0 44px;
  }

  .global-search-input-icon {
    left: 32px;
  }

  .global-search-clear-btn {
    right: 32px;
  }

  .global-search-recent-section,
  .global-search-results {
    padding: 0 20px 16px;
  }

  .global-search-recent-title {
    font-size: 14px;
  }

  .global-search-result-username {
    font-size: 13px;
  }
}

/* Mobile Small (480px and below) */
@media screen and (max-width: 480px) {
  .global-search-panel {
    width: 100%;
    max-width: 100%;
  }

  .global-search-panel-header {
    padding: 12px 16px;
  }

  .global-search-panel-title {
    font-size: 18px;
  }

  .global-search-input-container {
    padding: 12px 16px;
  }

  .global-search-input {
    height: 32px;
    padding: 0 32px 0 40px;
    font-size: 13px;
  }

  .global-search-input-icon {
    left: 28px;
    width: 18px;
    height: 18px;
  }

  .global-search-clear-btn {
    right: 28px;
  }

  .global-search-recent-section,
  .global-search-results {
    padding: 0 16px 12px;
  }

  .global-search-recent-title {
    font-size: 13px;
  }

  .global-search-recent-item {
    padding: 10px;
  }

  .global-search-recent-avatar {
    width: 36px;
    height: 36px;
  }

  .global-search-result-item {
    padding: 10px;
  }

  .global-search-result-avatar {
    width: 36px;
    height: 36px;
  }

  .global-search-result-username {
    font-size: 12px;
  }

  .global-search-result-fullname {
    font-size: 12px;
  }
}

/* Mobile Extra Small (320px) */
@media screen and (max-width: 320px) {
  .global-search-panel-header {
    padding: 10px 12px;
  }

  .global-search-panel-title {
    font-size: 16px;
  }

  .global-search-input-container {
    padding: 10px 12px;
  }

  .global-search-input {
    height: 30px;
    padding: 0 28px 0 36px;
    font-size: 12px;
  }

  .global-search-input-icon {
    left: 24px;
    width: 16px;
    height: 16px;
  }

  .global-search-clear-btn {
    right: 24px;
  }

  .global-search-recent-section,
  .global-search-results {
    padding: 0 12px 10px;
  }

  .global-search-recent-item {
    padding: 8px;
  }

  .global-search-recent-avatar {
    width: 32px;
    height: 32px;
  }

  .global-search-result-item {
    padding: 8px;
  }

  .global-search-result-avatar {
    width: 32px;
    height: 32px;
  }
}
