.mobile-menu-button {
  display: none;
  width: 44px;
  height: 44px;
  background: rgba(255, 255, 255, 0.1);
  border: none;
  border-radius: 12px;
  cursor: pointer;
  align-items: center;
  justify-content: center;
  color: #fff;
  transition: background 0.2s;
  flex-shrink: 0;
}

.mobile-menu-button:hover {
  background: rgba(255, 255, 255, 0.2);
}

.mobile-menu-button svg {
  width: 24px;
  height: 24px;
}

@media screen and (max-width: 768px) {
  .mobile-menu-button {
    display: flex;
  }
}

@media screen and (max-width: 480px) {
  .mobile-menu-button {
    width: 40px;
    height: 40px;
    border-radius: 10px;
  }

  .mobile-menu-button svg {
    width: 22px;
    height: 22px;
  }
}

@media screen and (max-width: 320px) {
  .mobile-menu-button {
    width: 36px;
    height: 36px;
    border-radius: 8px;
  }

  .mobile-menu-button svg {
    width: 20px;
    height: 20px;
  }
}
