+
+/* Responsive Styles */
+
+/* Desktop and Laptop (1024px - 1439px) */
+@media screen and (max-width: 1439px) {
+  .notifications-panel {
+    left: max(200px, calc(50% - 600px + 200px));
+    width: 360px;
+  }
+}
+
+/* Tablet Landscape (1024px) */
+@media screen and (max-width: 1024px) {
+  .notifications-panel {
+    left: 80px;
+    width: 340px;
+  }
+}
+
+/* Tablet Portrait and Mobile (768px and below) */
+@media screen and (max-width: 768px) {
+  .notifications-overlay {
+    display: none;
+  }
+
+  .notifications-panel {
+    position: fixed;
+    left: 0;
+    top: 0;
+    width: 100%;
+    max-width: 400px;
+    height: 100vh;
+    border-radius: 0;
+    transform: translateX(-100%);
+    transition: transform 0.3s ease;
+  }
+
+  .notifications-panel.open {
+    transform: translateX(0);
+    box-shadow: 4px 0 24px rgba(0, 0, 0, 0.15);
+  }
+
+  .notifications-panel-header {
+    padding: 20px;
+  }
+
+  .notifications-panel-title {
+    font-size: 20px;
+  }
+
+  .notifications-section-title {
+    padding: 14px 20px;
+    font-size: 14px;
+  }
+
+  .notification-item {
+    padding: 12px 20px;
+  }
+
+  .notification-avatar {
+    width: 40px;
+    height: 40px;
+  }
+
+  .notification-info {
+    font-size: 13px;
+    line-height: 16px;
+  }
+
+  .notification-post-thumb {
+    width: 40px;
+    height: 40px;
+  }
+}
+
+/* Mobile Small (480px and below) */
+@media screen and (max-width: 480px) {
+  .notifications-panel {
+    width: 100%;
+    max-width: 100%;
+  }
+
+  .notifications-panel-header {
+    padding: 16px;
+  }
+
+  .notifications-panel-title {
+    font-size: 18px;
+  }
+
+  .notifications-section-title {
+    padding: 12px 16px;
+    font-size: 13px;
+  }
+
+  .notification-item {
+    padding: 10px 16px;
+    gap: 10px;
+  }
+
+  .notification-avatar {
+    width: 36px;
+    height: 36px;
+  }
+
+  .notification-info {
+    font-size: 12px;
+    line-height: 14px;
+  }
+
+  .notification-action-btn {
+    padding: 5px 12px;
+    font-size: 12px;
+  }
+
+  .notification-post-thumb {
+    width: 36px;
+    height: 36px;
+  }
+}
+
+/* Mobile Extra Small (320px) */
+@media screen and (max-width: 320px) {
+  .notifications-panel-header {
+    padding: 12px;
+  }
+
+  .notifications-panel-title {
+    font-size: 16px;
+  }
+
+  .notification-item {
+    padding: 8px 12px;
+  }
+
+  .notification-avatar {
+    width: 32px;
+    height: 32px;
+  }
+
+  .notification-info {
+    font-size: 11px;
+  }
+
+  .notification-post-thumb {
+    width: 32px;
+    height: 32px;
+  }
+}

commits.content:
.notifications-overlay {
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.65);
  z-index: 998;
  animation: notifications-fadeIn 0.3s ease-out;
  cursor: pointer;
}

@keyframes notifications-fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.notifications-panel {
  position: fixed;
  left: max(244px, calc(50% - 720px + 244px));
  top: 0;
  width: 397px; 
  height: 100vh;
  background-color: #fff;
  border-right: 1px solid #dbdbdb;
  border-top-right-radius: 16px;
  border-bottom-right-radius: 16px;
  display: flex;
  flex-direction: column;
  z-index: 999;
  box-shadow: 4px 0 24px rgba(0, 0, 0, 0.15);
  animation: notifications-slideIn 0.3s cubic-bezier(0.1, 0.9, 0.2, 1);
  overflow: hidden;
  box-sizing: border-box;
}

@keyframes notifications-slideIn {
  from { transform: translateX(-100%); opacity: 0; }
  to { transform: translateX(0); opacity: 1; }
}

.notifications-panel-header {
  padding: 24px;
  border-bottom: none;
}

.notifications-panel-title {
  font-size: 24px;
  font-weight: 700;
  color: #000;
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
}

.notifications-panel-content {
  flex: 1;
  overflow-y: auto;
  padding: 0;
}

.notifications-section-title {
  padding: 16px 24px;
  font-size: 16px;
  font-weight: 600;
  color: #000;
  margin: 0;
}

.notifications-list {
  display: flex;
  flex-direction: column;
}

.notification-item {
  display: flex;
  align-items: center;
  padding: 12px 24px;
  gap: 12px;
  transition: background-color 0.2s;
  cursor: pointer;
}

.notification-item:hover {
  background-color: #fafafa;
}

.notification-avatar-container {
  position: relative;
  flex-shrink: 0;
}

.notification-avatar {
  width: 44px;
  height: 44px;
  border-radius: 50%;
  object-fit: cover;
  border: 1px solid rgba(0,0,0,0.1);
}

.notification-avatar-icon {
  position: absolute;
  bottom: -2px;
  right: -2px;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  border: 2px solid #fff;
}

.notification-info {
  flex: 1;
  font-size: 14px;
  line-height: 18px;
  color: #000;
  margin-right: 12px;
}

.notification-username {
  font-weight: 600;
}

.notification-time {
  color: #737373;
  margin-left: 4px;
}

.notification-action {
  flex-shrink: 0;
}

.notification-action-btn {
  background-color: #0095f6;
  color: #fff;
  border: none;
  border-radius: 8px;
  padding: 7px 16px;
  font-weight: 600;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.2s;
}

.notification-action-btn:hover {
  background-color: #1877f2;
}

.notification-action-btn.following {
  background-color: #efefef;
  color: #000;
}

.notification-action-btn.following:hover {
  background-color: #dbdbdb;
}

.notification-post-thumb {
  width: 44px;
  height: 44px;
  object-fit: cover;
  border-radius: 4px;
  border: 1px solid rgba(0,0,0,0.1);
}
