+/* Responsive Styles */
+
+/* Desktop and Laptop (1024px - 1439px) */
+@media screen and (max-width: 1439px) {
+  .post-modal {
+    max-width: 800px;
+    width: 95vw;
+  }
+
+  .post-modal-inner {
+    grid-template-columns: 1fr 280px;
+  }
+}
+
+/* Tablet Landscape (1024px) */
+@media screen and (max-width: 1024px) {
+  .post-modal {
+    width: 95vw;
+    max-width: 700px;
+  }
+
+  .post-modal-inner {
+    grid-template-columns: 1fr 260px;
+  }
+
+  .post-modal-side {
+    min-width: 200px;
+  }
+}
+
+/* Tablet Portrait and Mobile (768px and below) */
+@media screen and (max-width: 768px) {
+  .post-modal-overlay {
+    background: rgba(0, 0, 0, 0.85);
+  }
+
+  .post-modal {
+    position: fixed;
+    top: 0;
+    left: 0;
+    right: 0;
+    bottom: 0;
+    width: 100%;
+    height: 100%;
+    max-width: 100%;
+    max-height: 100%;
+    border-radius: 0;
+    transform: none;
+  }
+
+  .post-modal-inner {
+    grid-template-columns: 1fr;
+    grid-template-rows: 1fr auto;
+    max-height: 100%;
+    height: 100%;
+  }
+
+  .post-modal-media {
+    min-height: 40vh;
+    max-height: 50vh;
+  }
+
+  .post-modal-side {
+    max-height: 50vh;
+    border-left: none;
+    border-top: 1px solid #9e9e9e;
+  }
+
