+    right: 8px;
+    width: 32px;
+    height: 32px;
+    font-size: 20px;
+  }
+
+  .post-modal-header {
+    padding: 12px 16px;
+  }
+
+  .post-modal-username {
+    font-size: 14px;
+  }
+}
+
+/* Mobile Small (480px and below) */
+@media screen and (max-width: 480px) {
+  .post-modal {
+    width: 100%;
+    height: 100%;
+  }
+
+  .post-modal-inner {
+    grid-template-rows: 1fr auto;
+  }
+
+  .post-modal-media {
+    min-height: 35vh;
+    max-height: 45vh;
+  }
+
+  .post-modal-side {
+    max-height: 55vh;
+  }
+
+  .post-modal-close {
+    width: 28px;
+    height: 28px;
+    font-size: 18px;
+    top: 6px;
+    right: 6px;
+  }
+
+  .post-modal-header {
+    padding: 10px 12px;
+  }
+
+  .post-modal-username {
+    font-size: 13px;
+  }
+
+  .post-modal-comment {
+    margin-bottom: 10px;
+  }
+
+  .post-modal-comment-avatar {
+    width: 28px;
+    height: 28px;
+  }
+
+  .post-modal-comment-username {
+    font-size: 13px;
+  }
+
+  .post-modal-comment-text {
+    font-size: 13px;
+  }
+
+  .post-modal-caption {
+    font-size: 13px;
+  }
+
+  .post-modal-add-comment {
+    padding: 10px 12px;
+  }
+
+  .post-modal-input {
