+    font-size: 13px;
+  }
+}
+
+/* Mobile Extra Small (320px) */
+@media screen and (max-width: 320px) {
+  .post-modal-media {
+    min-height: 30vh;
+    max-height: 40vh;
+  }
+
+  .post-modal-side {
+    max-height: 60vh;
+  }
+
+  .post-modal-header {
+    padding: 8px 10px;
+  }
+
+  .post-modal-username {
+    font-size: 12px;
+  }
+
+  .post-modal-comment-avatar {
+    width: 24px;
+    height: 24px;
+  }
+
+  .post-modal-comment-username {
+    font-size: 12px;
+  }
+
+  .post-modal-comment-text {
+    font-size: 12px;
+  }
+
+  .post-modal-caption {
+    font-size: 12px;
+  }
+
+  .post-modal-submit {
+    padding: 6px 12px;
+    font-size: 12px;
