+
+/* Responsive Styles */
+
+/* Desktop and Laptop (1024px - 1439px) */
+@media screen and (max-width: 1439px) {
+  .edit-profile-sidebar {
+    width: 200px;
+    left: max(0px, calc(50% - 600px));
+  }
+
+  .edit-profile-main {
+    margin-left: 200px;
+    padding: 30px;
+  }
+}
+
+/* Tablet Landscape (1024px) */
+@media screen and (max-width: 1024px) {
+  .edit-profile-sidebar {
+    width: 80px;
+    left: 0;
+  }
+
+  .edit-profile-main {
+    margin-left: 80px;
+    padding: 24px;
+  }
+
+  .edit-profile-sidebar-nav {
+    padding: 0 8px;
+  }
+
+  .edit-profile-nav-item {
+    justify-content: center;
+    padding: 12px 8px;
+    gap: 0;
+  }
+
+  .edit-profile-nav-text {
+    display: none;
+  }
+
+  .edit-profile-sidebar-current-user {
+    justify-content: center;
+    padding: 10px 8px;
+    margin-top: 16px;
+  }
+
+  .edit-profile-sidebar-current-info {
+    display: none;
+  }
+}
+
+/* Tablet Portrait and Mobile (768px and below) */
+@media screen and (max-width: 768px) {
+  .edit-profile-sidebar {
+    position: fixed;
+    left: -244px;
+    width: 244px;
+    height: 100vh;
+    z-index: 1000;
+    transition: left 0.3s ease;
+    padding-top: 60px;
+  }
+
+  .edit-profile-sidebar.open {
+    left: 0;
+  }
+
+  .edit-profile-sidebar-logo {
+    display: block;
+    font-size: 28px;
+    padding: 0 20px;
+    margin-bottom: 24px;
+  }
+
+  .edit-profile-sidebar-nav {
+    padding: 0 16px;
+  }
+
+  .edit-profile-nav-item {
+    justify-content: flex-start;
+    padding: 14px 16px;
+    gap: 16px;
+  }
+
+  .edit-profile-nav-text {
+    display: block;
+    font-size: 16px;
+  }
+
+  .edit-profile-sidebar-current-user {
+    justify-content: flex-start;
+    padding: 12px 16px;
+    margin-top: 20px;
+  }
+
+  .edit-profile-sidebar-current-info {
+    display: flex;
+  }
+
+  .edit-profile-main {
+    margin-left: 0;
+    padding: 20px;
+    min-height: calc(100vh - 60px);
+  }
+
+  .edit-profile-content {
+    max-width: 100%;
+  }
+
+  .edit-profile-form {
+    gap: 20px;
+  }
+
+  .edit-profile-avatar-section {
+    flex-direction: column;
+    align-items: flex-start;
+    gap: 16px;
+  }
+
+  .edit-profile-actions {
+    flex-wrap: wrap;
+  }
+}
+
+/* Mobile Small (480px and below) */
+@media screen and (max-width: 480px) {
+  .edit-profile-sidebar {
+    width: 280px;
+    left: -280px;
+    padding-top: 50px;
+  }
+
+  .edit-profile-sidebar.open {
+    left: 0;
+  }
+
+  .edit-profile-sidebar-logo {
+    font-size: 26px;
+    padding: 0 16px;
+  }
+
+  .edit-profile-nav-item {
+    padding: 12px 14px;
+    font-size: 15px;
+  }
+
+  .edit-profile-sidebar-current-avatar {
+    width: 36px;
+    height: 36px;
+  }
+
+  .edit-profile-main {
+    padding: 16px;
+  }
+
+  .edit-profile-title {
+    font-size: 24px;
+  }
+
+  .edit-profile-form {
+    gap: 16px;
+  }
+
+  .edit-profile-field {
+    gap: 6px;
+  }
+
+  .edit-profile-label {
+    font-size: 13px;
+  }
+
+  .edit-profile-input {
+    height: 36px;
+    font-size: 14px;
+  }
+
+  .edit-profile-textarea {
+    min-height: 180px;
+    font-size: 14px;
+  }
+
+  .edit-profile-actions {
+    gap: 12px;
+  }
+
+  .edit-profile-save-btn {
+    padding: 8px 24px;
+    font-size: 14px;
+  }
+
+  .edit-profile-delete-btn {
+    padding: 8px 20px;
+    font-size: 14px;
+  }
+}
+
+/* Mobile Extra Small (320px) */
+@media screen and (max-width: 320px) {
+  .edit-profile-sidebar {
+    width: 100%;
+    left: -100%;
+  }
+
+  .edit-profile-nav-item {
+    padding: 12px;
+    font-size: 14px;
+  }
+
+  .edit-profile-main {
+    padding: 12px;
+  }
+
+  .edit-profile-title {
+    font-size: 20px;
+  }
+
+  .edit-profile-input {
+    height: 32px;
+    font-size: 12px;
+  }
+
+  .edit-profile-textarea {
+    min-height: 150px;
+    font-size: 12px;
+  }
+
+  .edit-profile-save-btn,
+  .edit-profile-delete-btn {
+    padding: 6px 16px;
+    font-size: 12px;
+  }
+}

