commits.content:
.home-container {
  width: 1440px;
  min-height: 100vh;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  background-color: #fff;
  box-sizing: border-box;
  position: relative;
  overflow: visible;
}


.home-top-bar {
  width: 100%;
  height: 30px;
  background-color: #2C3A47;
  display: flex;
  align-items: center;
  padding: 0 16px;
  box-sizing: border-box;
  flex-shrink: 0;
}

.home-top-text {
  color: #fff;
  font-size: 12px;
  font-family: 'Inter', sans-serif;
}


.home-layout {
  display: flex;
  flex: 1;
  min-height: 100vh;
  position: relative;
  overflow: visible;
  margin-left: 244px; 
}


.home-sidebar {
  width: 244px;
  background-color: #2C3A47;
  display: flex;
  flex-direction: column;
  padding: 20px 0;
  box-sizing: border-box;
  flex-shrink: 0;
  border-right: 1px solid #DDC9D4;
  position: fixed;
  top: 0;
  left: calc(50% - 720px); 
  height: 100vh;
  overflow-y: auto;
  z-index: 101;
}

.home-sidebar-logo {
  font-family: 'Praise', cursive;
  font-size: 32px;
  font-weight: 400;
  color: #fff;
  padding: 0 24px;
  margin-bottom: 30px;
}

.home-sidebar-nav {
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 0 12px;
}


.home-sidebar-current-user {
  margin-top: 24px;
  padding: 10px 16px;
  display: flex;
  align-items: center;
  gap: 10px;
  text-decoration: none;
  color: #ffffff;
  font-family: 'Inter', sans-serif;
}

.home-sidebar-current-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  overflow: hidden;
  background-color: #DDC9D4;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

.home-sidebar-current-avatar img {
  width: 100%;
  height: 100%;
  object-fit: contain; 
  border-radius: 50%;
}

.home-sidebar-current-avatar video {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
}

.home-sidebar-current-initial {
  font-size: 16px;
  font-weight: 600;
  color: #2C3A47;
}

.home-sidebar-current-info {
  display: flex;
  flex-direction: column;
}

.home-sidebar-current-name {
  font-size: 14px;
  font-weight: 600;
}

.home-sidebar-current-username {
  font-size: 12px;
  color: #B8B0C1;
}

.home-nav-item {
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 12px;
  text-decoration: none;
  color: #fff;
  font-size: 16px;
  font-family: 'Inter', sans-serif;
  border-radius: 8px;
  transition: all 0.2s;
}

.home-nav-item:hover {
  background-color: rgba(0, 169, 190, 0.1);
  color: #00A9BE;
}

.home-nav-item.active {
  background-color: rgba(0, 169, 190, 0.15);
  color: #00A9BE;
  font-weight: 600;
}

.home-nav-icon {
  width: 24px;
  height: 24px;
  color: inherit;
  flex-shrink: 0;
  transition: color 0.2s;
}


.home-sidebar-bubbles {
  position: relative;
  flex: 1;
  width: 100%;
  overflow: hidden;
  margin-top: auto;
}


.home-bubble {
  position: absolute;
  border-radius: 50%;
  background: radial-gradient(
    circle at 30% 30%,
    rgba(0, 169, 190, 0.4) 0%,
    rgba(221, 201, 212, 0.3) 30%,
    rgba(184, 176, 193, 0.25) 60%,
    rgba(247, 232, 228, 0.2) 100%
  );
  border: 1px solid rgba(221, 201, 212, 0.3);
  backdrop-filter: blur(2px);
  animation: bubbleFloat 15s infinite ease-in-out;
  opacity: 0.7;
  box-shadow: 
    inset 0 0 20px rgba(0, 169, 190, 0.2),
    0 0 10px rgba(221, 201, 212, 0.15);
}

.home-bubble-1 {
  width: 30px;
  height: 30px;
  left: 20%;
  bottom: 10%;
  animation-delay: 0s;
  animation-duration: 18s;
}

.home-bubble-2 {
  width: 45px;
  height: 45px;
  left: 60%;
  bottom: 15%;
  animation-delay: 2s;
  animation-duration: 20s;
}

.home-bubble-3 {
  width: 25px;
  height: 25px;
  left: 40%;
  bottom: 5%;
  animation-delay: 4s;
  animation-duration: 16s;
}

.home-bubble-4 {
  width: 35px;
  height: 35px;
  left: 75%;
  bottom: 8%;
  animation-delay: 6s;
  animation-duration: 22s;
}

.home-bubble-5 {
  width: 40px;
  height: 40px;
  left: 10%;
  bottom: 12%;
  animation-delay: 8s;
  animation-duration: 19s;
}

.home-bubble-6 {
  width: 28px;
  height: 28px;
  left: 50%;
  bottom: 3%;
  animation-delay: 10s;
  animation-duration: 17s;
}

@keyframes bubbleFloat {
  0% {
    transform: translateY(0) translateX(0) scale(1);
    opacity: 0.6;
  }
  25% {
    transform: translateY(-25vh) translateX(10px) scale(1.1);
    opacity: 0.8;
  }
  50% {
    transform: translateY(-50vh) translateX(-5px) scale(0.9);
    opacity: 0.7;
  }
  75% {
    transform: translateY(-75vh) translateX(8px) scale(1.05);
    opacity: 0.5;
  }
  100% {
    transform: translateY(-100vh) translateX(0) scale(0.8);
    opacity: 0;
  }
}


.home-main {
  flex: 1;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  overflow-y: auto;
  transition: opacity 0.3s ease-out;
  position: relative;
  z-index: 1;
}

.home-main-dimmed {
  opacity: 0.7;
}


.home-notifications-panel {
  position: fixed;
  left: 484px;
  top: 0;
  width: 400px;
  height: 100vh;
  background-color: #ffffff;
  border-right: 1px solid #DDC9D4;
  display: flex;
  flex-direction: column;
  z-index: 102;
  box-shadow:
    2px 0 8px rgba(0, 0, 0, 0.1),
    0 0 0 1px rgba(255, 255, 255, 0.9);
  animation: slideInFromLeft 0.3s ease-out;
  border-top-right-radius: 15px;
  border-bottom-right-radius: 15px;
}

.home-notifications-header {
  padding: 20px 24px;
  border-bottom: 1px solid #DDC9D4;
  background-color: #fff;
}

.home-notifications-title {
  font-size: 24px;
  font-weight: 600;
  color: #2C3A47;
  font-family: 'Inter', sans-serif;
  margin: 0;
}

.home-notifications-section {
  flex: 1;
  padding: 16px 24px 24px;
  overflow-y: auto;
}

.home-notifications-subtitle {
  font-size: 16px;
  font-weight: 600;
  color: #2C3A47;
  font-family: 'Inter', sans-serif;
  margin: 0 0 16px 0;
}

.home-notifications-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.home-notification-item {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 8px 0;
  border-radius: 8px;
  cursor: pointer;
  border: none;
  width: 100%;
  background: none;
  text-align: left;
}

.home-notification-user {
  display: flex;
  align-items: center;
  gap: 12px;
}

.home-notification-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  overflow: hidden;
  flex-shrink: 0;
}

.home-notification-avatar img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.home-notification-text {
  display: flex;
  flex-direction: column;
  font-family: 'Inter', sans-serif;
  font-size: 14px;
  color: #2C3A47;
}

.home-notification-username {
  font-weight: 600;
}

.home-notification-action {
  font-weight: 400;
}

.home-notification-time {
  font-size: 12px;
  color: #B8B0C1;
  margin-top: 2px;
}

.home-notification-preview {
  width: 44px;
  height: 44px;
  border-radius: 10px;
  overflow: hidden;
  flex-shrink: 0;
}

.home-notification-preview img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}


.home-create-modal {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 953px;  
  max-width: 95%;
  height: 604px; 
  max-height: 90vh;
  background-color: #ffffff;
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.25);
  z-index: 200;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.home-create-modal-header {
  height: 48px;
  border-bottom: 1px solid #dbdbdb;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  font-family: 'Inter', sans-serif;
}

.home-create-modal-title {
  font-size: 16px;
  font-weight: 600;
  color: #262626;
}

.home-create-modal-share {
  position: absolute;
  right: 16px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: #0095f6;
  font-size: 14px;
  font-weight: 600;
  font-family: 'Inter', sans-serif;
  cursor: pointer;
}

.home-create-modal-share:disabled {
  color: #b3dffc;
  cursor: default;
}

.home-create-modal-body {
  display: grid;
  grid-template-columns: 2fr 1.2fr;
  flex: 1;
  min-height: 0;
}

.home-create-modal-left {
  border-right: 1px solid #dbdbdb;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #fafafa;
}

.home-create-upload-area {
  width: 90%;
  height: 80%;
  border-radius: 16px;
  border: 2px dashed #dbdbdb;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 12px;
  cursor: pointer;
  color: #8e8e8e;
  font-family: 'Inter', sans-serif;
}

.home-create-upload-icon svg {
  width: 40px;
  height: 40px;
  color: #8e8e8e;
}

.home-create-upload-text {
  font-size: 14px;
}

.home-create-file-input {
  display: none;
}

.home-create-image-preview {
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #000;
  position: relative;
}

.home-create-main-image {
  width: 100%;
  height: calc(100% - 120px);
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.home-create-main-image img,
.home-create-main-image .home-create-preview-video {
  max-width: 100%;
  max-height: 100%;
  object-fit: contain;
}

.home-create-main-image .home-create-preview-video {
  width: 100%;
  height: 100%;
  display: block;
}

.home-create-remove-image {
  position: absolute;
  top: 10px;
  right: 10px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: rgba(0, 0, 0, 0.6);
  color: white;
  border: none;
  font-size: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
  transition: background-color 0.2s;
}

.home-create-remove-image:hover {
  background-color: rgba(0, 0, 0, 0.8);
}

.home-create-nav-arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: rgba(255, 255, 255, 0.8);
  color: #262626;
  border: none;
  font-size: 24px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
  transition: all 0.2s;
}

.home-create-nav-arrow:hover {
  background-color: rgba(255, 255, 255, 1);
  transform: translateY(-50%) scale(1.1);
}

.home-create-nav-arrow-left {
  left: 10px;
}

.home-create-nav-arrow-right {
  right: 10px;
}

.home-create-image-counter {
  position: absolute;
  bottom: 10px;
  left: 50%;
  transform: translateX(-50%);
  background-color: rgba(0, 0, 0, 0.6);
  color: white;
  padding: 4px 12px;
  border-radius: 12px;
  font-size: 12px;
  font-family: 'Inter', sans-serif;
}

.home-create-thumbnails {
  display: flex;
  gap: 8px;
  padding: 10px;
  justify-content: center;
  align-items: center;
  height: 80px;
  background-color: rgba(0, 0, 0, 0.3);
  overflow-x: auto;
}

.home-create-thumbnail {
