}

.home-create-thumbnail img,
.home-create-thumbnail .home-create-thumbnail-video {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.home-create-add-more {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px;
  cursor: pointer;
}

.home-create-add-more-btn {
  padding: 8px 16px;
  background-color: #00A9BE;
  color: white;
  border-radius: 8px;
  font-size: 14px;
  font-family: 'Inter', sans-serif;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;
}

.home-create-add-more-btn:hover {
  background-color: #0095a6;
}

.home-create-video {
  width: 100%;
  height: 100%;
  object-fit: contain;
}

.home-create-modal-right {
  display: flex;
  flex-direction: column;
  padding: 16px;
  font-family: 'Inter', sans-serif;
}

.home-create-user-row {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 12px;
}

.home-create-user-avatar {
  width: 32px;
  height: 32px;
}

.home-create-username {
