}

.home-post-caption-username {
  font-weight: 600;
}


.home-post-more-button {
  border: none;
  background: none;
  padding: 0;
  margin-left: 4px;
  color: #B8B0C1;
  font-size: 14px;
  font-family: 'Inter', sans-serif;
  cursor: pointer;
}

.home-post-more-button:hover {
  color: #2C3A47;
}


.home-post-view-comments {
  background: none;
  border: none;
  color: #B8B0C1;
  font-size: 14px;
  font-family: 'Inter', sans-serif;
  text-align: left;
  padding: 0 16px 16px;
  cursor: pointer;
  transition: color 0.2s;
}

.home-post-view-comments:hover {
  color: #00A9BE;
}


.home-updates-section {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 60px 20px;
  text-align: center;
}

.home-updates-icon {
