+    margin-left: 200px;
+  }
+
+  .notifications-list {
+    gap: 8px;
+  }
+}
+
+/* Tablet Landscape (1024px) */
+@media screen and (max-width: 1024px) {
+  .notifications-sidebar {
+    width: 80px;
+    left: 0;
+  }
+
+  .notifications-main {
+    margin-left: 80px;
+    padding: 16px;
+  }
+
+  .notifications-sidebar-nav {
+    padding: 0 8px;
+  }
+
+  .notifications-nav-item {
+    justify-content: center;
+    padding: 12px 8px;
+    gap: 0;
+  }
+
+  .notifications-nav-text {
+    display: none;
+  }
+
+  .notifications-sidebar-current-user {
+    justify-content: center;
+    padding: 10px 8px;
+    margin-top: 16px;
+  }
+
+  .notifications-sidebar-current-info {
+    display: none;
+  }
+
+  .notifications-item {
+    padding: 12px;
+  }
+}
+
+/* Tablet Portrait and Mobile (768px and below) */
+@media screen and (max-width: 768px) {
+  .notifications-sidebar {
+    position: fixed;
+    left: -244px;
+    width: 244px;
+    height: 100vh;
+    z-index: 1000;
+    transition: left 0.3s ease;
+    padding-top: 60px;
+  }
+
+  .notifications-sidebar.open {
+    left: 0;
+  }
+
+  .notifications-sidebar-logo {
+    display: block;
+    font-size: 28px;
+    padding: 0 20px;
+    margin-bottom: 24px;
+  }
+
+  .notifications-sidebar-nav {
+    padding: 0 16px;
+  }
+
+  .notifications-nav-item {
+    justify-content: flex-start;
+    padding: 14px 16px;
+    gap: 16px;
+  }
+
+  .notifications-nav-text {
+    display: block;
+    font-size: 16px;
+  }
+
+  .notifications-sidebar-current-user {
+    justify-content: flex-start;
+    padding: 12px 16px;
+    margin-top: 20px;
+  }
+
+  .notifications-sidebar-current-info {
+    display: flex;
+  }
+
+  .notifications-main {
+    margin-left: 0;
+    padding: 16px;
+  }
+
+  .notifications-header {
+    padding: 16px;
+  }
+
+  .notifications-title {
+    font-size: 20px;
+  }
+
+  .notifications-list {
+    gap: 6px;
+  }
+
+  .notifications-item {
+    padding: 10px;
+  }
+
+  .notifications-avatar {
+    width: 44px;
+    height: 44px;
+  }
+
+  .notifications-text {
+    font-size: 13px;
+  }
+
+  .notifications-modal {
+    width: 100%;
+    height: 100%;
+    max-width: 100%;
+    max-height: 100%;
+    border-radius: 0;
+  }
+
+  .notifications-modal-inner {
+    grid-template-columns: 1fr;
+    grid-template-rows: 1fr auto;
+    max-height: 100%;
+  }
+}
+
+/* Mobile Small (480px and below) */
+@media screen and (max-width: 480px) {
+  .notifications-sidebar {
+    width: 280px;
+    left: -280px;
+    padding-top: 50px;
+  }
+
+  .notifications-sidebar.open {
+    left: 0;
+  }
+
+  .notifications-sidebar-logo {
+    font-size: 26px;
+    padding: 0 16px;
+  }
+
+  .notifications-nav-item {
+    padding: 12px 14px;
+    font-size: 15px;
+  }
+
+  .notifications-sidebar-current-avatar {
+    width: 36px;
+    height: 36px;
+  }
+
+  .notifications-main {
+    padding: 12px;
+  }
+
+  .notifications-header {
+    padding: 12px;
+  }
+
+  .notifications-title {
+    font-size: 18px;
+  }
+
+  .notifications-item {
+    padding: 8px;
+  }
+
+  .notifications-avatar {
+    width: 40px;
+    height: 40px;
+  }
+
+  .notifications-text {
+    font-size: 12px;
+  }
+}
+
+/* Mobile Extra Small (320px) */
+@media screen and (max-width: 320px) {
+  .notifications-sidebar {
+    width: 100%;
+    left: -100%;
+  }
+
+  .notifications-nav-item {
+    padding: 12px;
+    font-size: 14px;
+  }
+
+  .notifications-item {
+    padding: 6px;
+  }
+
+  .notifications-avatar {
+    width: 36px;
+    height: 36px;
+  }
+}

