.reset-container {
  width: 1440px;
  height: 900px;
  margin: 0 auto;
  background-color: #fafafa;
  display: flex;
  flex-direction: column;
  position: relative;
  overflow: hidden;
}

.reset-header {
  padding: 20px 40px;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 10;
}

.reset-page-title {
  font-size: 16px;
  font-weight: 400;
  color: #737373;
  font-family: 'Inter', sans-serif;
  margin: 0;
}

.reset-layout {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
}

.reset-box {
  width: 350px;
  min-height: 500px;
  background-color: #fff;
  border: 1px solid #dbdbdb;
  border-radius: 1px;
  padding: 40px;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-sizing: border-box;
}

.reset-logo {
  font-family: 'Praise', cursive;
  font-size: 42px;
  font-weight: 400;
  color: #2C3A47;
  margin-bottom: 30px;
  text-align: center;
}

.reset-icon {
  width: 96px;
  height: 96px;
  margin: 0 auto 24px;
  color: #2C3A47;
  display: flex;
  align-items: center;
  justify-content: center;
}

.reset-icon svg {
  width: 100%;
  height: 100%;
  stroke-width: 1.5;
}

.reset-icon-success {
  color: #00A9BE;
}

.reset-title {
  font-size: 20px;
  font-weight: 600;
  color: #2C3A47;
  font-family: 'Inter', sans-serif;
  text-align: center;
  margin: 0 0 16px;
  line-height: 1.4;
}

.reset-description {
  font-size: 14px;
  color: #737373;
  font-family: 'Inter', sans-serif;
  text-align: center;
  margin: 0 0 24px;
  line-height: 1.5;
}

.reset-form {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 12px;
  margin-bottom: 20px;
}

.reset-input {
  width: 100%;
  height: 38px;
  padding: 0 12px;
  background-color: #fafafa;
  border: 1px solid #dbdbdb;
  border-radius: 5px;
  font-size: 14px;
  font-family: 'Inter', sans-serif;
  color: #2C3A47;
  outline: none;
  box-sizing: border-box;
  transition: border-color 0.2s;
}

.reset-input:focus {
  border-color: #00A9BE;
  background-color: #fff;
}

.reset-input::placeholder {
  color: #8e8e8e;
}

.reset-error {
  font-size: 13px;
  color: #ed4956;
  font-family: 'Inter', sans-serif;
  text-align: center;
  margin-top: -8px;
}

.reset-button {
  width: 100%;
  height: 32px;
  background-color: #00A9BE;
  color: #fff;
  border: none;
  border-radius: 5px;
  font-size: 14px;
  font-weight: 600;
  font-family: 'Inter', sans-serif;
  cursor: pointer;
  transition: background-color 0.2s;
  margin-top: 8px;
}

.reset-button:hover:not(:disabled) {
  background-color: #0099aa;
}

.reset-button:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.reset-or {
  width: 100%;
  display: flex;
  align-items: center;
  gap: 16px;
  margin: 20px 0;
}

.reset-or-line {
  flex: 1;
  height: 1px;
  background-color: #dbdbdb;
  min-width: 0;
}

.reset-or-text {
  font-size: 13px;
  font-weight: 600;
  color: #8e8e8e;
  font-family: 'Inter', sans-serif;
  text-transform: uppercase;
}

.reset-create-account {
  width: 100%;
  height: 32px;
  background-color: #fff;
  color: #00A9BE;
  border: 1px solid #dbdbdb;
  border-radius: 5px;
  font-size: 14px;
  font-weight: 600;
  font-family: 'Inter', sans-serif;
  text-decoration: none;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.2s;
  margin-bottom: 20px;
}

.reset-create-account:hover {
  background-color: #fafafa;
}

.reset-back-login {
  width: 100%;
  height: 32px;
  background-color: #fafafa;
  color: #2C3A47;
  border: 1px solid #dbdbdb;
  border-radius: 5px;
  font-size: 14px;
  font-weight: 500;
  font-family: 'Inter', sans-serif;
  text-decoration: none;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.2s;
}

.reset-back-login:hover {
  background-color: #f0f0f0;
}
